import React, { useState, useEffect } from "react";
import { Link, useParams } from "react-router-dom";
import axios from "axios";

const User = () => {
  const [user, setUser] = useState({
    full_name: "",
    city: "",
    mobile_no: "",
    qualification: "",
    email: "",
    experience: ""
  });
  const { id } = useParams();
  useEffect(() => {
    loadUser();
  }, []);
  const loadUser = async () => {
    const res = await axios.get(`http://127.0.0.1:8000/api/${id}/detail/`);
    console.log(res);
    setUser(res.data);
  };

  const updateStatus = async (id,updatedstatus) => {

    await axios.patch(`http://127.0.0.1:8000/api/${id}/${updatedstatus}/updatestatus`);
    loadUser();
  };


  return (
    <div className="container py-4">
      <Link className="btn btn-primary" to="/">
        back to Home
      </Link>
      <h1 className="display-4">User Id: {id}</h1>
      <hr />
      <ul className="list-group w-50">
        <li className="list-group-item">name: {user.full_name}</li>
        <li className="list-group-item">email: {user.email}</li>
        <li className="list-group-item">mobile: {user.mobile_no}</li>
        <li className="list-group-item">city: {user.city}</li>
        <li className="list-group-item">qualification: {user.qualification}</li>
        <li className="list-group-item">experience: {user.experience}</li>
        <li className="list-group-item">status: {user.status}</li>
      </ul>

      <div>

          <Link
            class="btn btn-danger m-2"
            onClick={() => updateStatus(user.id,"Accepted")}
            to="/"
          >
            Accept
          </Link>

          <Link
            class="btn btn-danger m-2"
            onClick={() => updateStatus(user.id,"Rejected")}
            to="/"
          >
            Reject
          </Link>
        </div>





        <table class="table table-hover">
        <thead>
    <tr>
      <th scope="col">#</th>
      <th scope="col">First</th>
      <th scope="col">Last</th>
      <th scope="col">Handle</th>
    </tr>
  </thead>
  <tbody>
    <tr>
      <th scope="row">1</th>
      <td>Mark</td>
      <td>Otto</td>
      <td>@mdo</td>
    </tr>
    <tr>
      <th scope="row">2</th>
      <td>Jacob</td>
      <td>Thornton</td>
      <td>@fat</td>
    </tr>
    <tr>
      <th scope="row">3</th>
      <td colspan="2">Larry the Bird</td>
      <td>@twitter</td>
    </tr>
  </tbody>

</table>
    </div>
  );
};

export default User;
