import React, { useState, useEffect } from "react";
import { Link, useParams } from "react-router-dom";
import axios from "axios";

const User = () => {
  const [user, setUser] = useState({
    name: "",
    username: "",
    email: "",
    phone: "",
    webiste: ""
  });
  const { id } = useParams();
  useEffect(() => {
    loadUser();
  }, []);
  const loadUser = async () => {
    const res = await axios.get(`http://127.0.0.1:8000/api/${id}/detail/`);
    console.log(res);
    setUser(res.data);
  };

  const updateStatus = async (id,updatedstatus) => {

    await axios.post(`http://127.0.0.1:8000/api/${id}/updatestatus/`, updatedstatus);
    loadUser();
  };


  return (
    <div className="container py-4">
      <Link className="btn btn-primary" to="/">
        back to Home
      </Link>
      <h1 className="display-4">User Id: {id}</h1>
      <hr />
      <ul className="list-group w-50">
        <li className="list-group-item">name: {user.full_name}</li>
        <li className="list-group-item">mobile: {user.mobile_no}</li>
        <li className="list-group-item">city: {user.city}</li>
        <li className="list-group-item">status: {user.status}</li>
      </ul>

      <div>

          <Link
            class="btn btn-danger"
            onClick={() => updateStatus(user.id,"AC")}
            to="/"
          >
            Accept
          </Link>
        </div>
    </div>
  );
};

export default User;
